(()=>{"use strict";const e=document.getElementById("search-input"),t=document.getElementById("weather-form"),n=document.getElementById("weather-image"),i=document.getElementById("temp"),s=document.getElementById("feels-like"),d=document.getElementById("humidity"),o=document.getElementById("windspeed"),r=document.getElementById("city");t.addEventListener("submit",(t=>{t.preventDefault(),(async()=>{let t=await(async e=>{try{const t=await fetch(`https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/${e}?unitGroup=metric&key=CYAVPQ3GA7FLJK9A8GQV4DEYW`,{mode:"cors"});if(!t.ok)throw new Error(`"${e}" was not found`);return(e=>{const{resolvedAddress:t,currentConditions:{temp:n,feelslike:i,humidity:s,windspeed:d,icon:o},description:r}=e;return{address:t,temp:n,feelslike:i,humidity:s,windspeed:d,description:r,icon:o}})(await t.json())}catch(e){return alert("Something went wrong while fetching the data.\n"+e),null}})(e.value);if(!t)return;let c=t.address.split(", ");c.length>=3?r.textContent=c[0]+", "+c[2]:r.textContent=t.address,n.src=`https://raw.githubusercontent.com/visualcrossing/WeatherIcons/main/SVG/2nd%20Set%20-%20Color/${t.icon}.svg`,i.textContent=`${t.temp}°C`,s.textContent=`Feels Like: ${t.feelslike}°C`,d.textContent=`Humidity: ${t.humidity}%`,o.textContent=`Windspeed: ${t.windspeed}km/h`})()}))})();